<?phpnamespace App\Http\Controllers\Admin;use App\Http\Models\Article;use Carbon\Carbon;use Illuminate\Http\Request;use Illuminate\Support\Facades\View;class ArticleController extends CommonController{    public function index(Article $article)    {        $article_list = $article                        ->where('published_at','<=',Carbon::now())                        ->orderBy('published_at','desc')                        ->paginate(9);        foreach ($article_list as $key => $article)        {            $article_list[$key]['tag'] = explode(',',$article['tag']);        }        return view('Admin.Article.index')->with(compact('article_list'));    }    public function show(Article $article,Request $request)    {        $slug = $request->slug;        $article = $article::whereSlug($slug)                    ->where('published_at','<=',Carbon::now())                    ->firstOrFail();        $article['tag'] = explode(',',$article['tag']);        return view('Admin.Article.show')->with(compact('article'));    }}