<?phpnamespace App\Http\Controllers;use Illuminate\Foundation\Bus\DispatchesJobs;use Illuminate\Routing\Controller as BaseController;use Illuminate\Foundation\Validation\ValidatesRequests;use Illuminate\Foundation\Auth\Access\AuthorizesRequests;use Illuminate\Support\Facades\Route;abstract class Controller extends BaseController{    use AuthorizesRequests, DispatchesJobs, ValidatesRequests;    public function __construct()    {    }    protected function getCurrentRoute()    {        $route['route'] = Route::currentRouteAction();        $route_array = explode('\\',$route['route']);        $c_a = $route_array[count($route_array)-1];        list($route['controller'],$route['action']) = explode('@',$c_a);        $route['module'] = count($route_array) == 5 ? $route_array[3] : '';        return $route;    }    //获取请求的方法    protected function requestMethod()    {        $request = new Request();        return strtoupper($request->method());    }}