<?phpnamespace App\Http\Service;use App\Http\Repository\AdminRepository;use Illuminate\Http\Request;class AdminService{    protected $repository;    public function __construct(AdminRepository $adminRepository)    {        $this->repository = $adminRepository;    }    public function get()    {            }    public function edit($id,Request $request,QiniuService $qiniuService)    {        $data = $request->only('nickname','email','password','position','status');        $data['avatar'] = $qiniuService->upload($request->file('avatar'));        $data['status'] = $data['status'] == 'on' ? 1 : 0;        return $this->repository->editAdminData($id,$data);    }}